class A11yButton{constructor(t){this.button=t,this.button.setAttribute("aria-expanded","false"),this.button.addEventListener("click",(()=>{this.toggleButton()}))}toggleButton(){const t="true"===this.button.getAttribute("aria-expanded");this.button.setAttribute("aria-expanded",String(!t))}}const navButton=document.getElementById("navigation-button");navButton&&new A11yButton(navButton);